    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        UsuarioDAO controller = new UsuarioDAO();
        
        String email = jTextFieldLogin.getText();
        String senha = jPasswordFieldPass.getText();
        
        if (email.equals("") || senha.equals("")) {
            Prompts.promptAlerta("Preencha login e senha!");
        } else {
            Usuario usuario = new Usuario();
            usuario.setEmail(email);
            usuario.setSenha(senha);
            
            if (new UsuarioDAO().autenticar(usuario)) {
                new TelaPrincipal().setVisible(true);
                this.dispose();
            } else {
                Prompts.promptAlerta("Dados inv√°lidos!");
            }
        }        
    }//GEN-LAST:event_jButton1ActionPerformed

